mapper.select:
SELECT e.e_id, DATE_FORMAT(e.vaccum_date, '%y-%m-%d') as vaccum_date, e.work_type, (select code_name from common_code where code = e.work_type AND code_group='work_type') as work_type_name, e.vaccum, (select code_name from common_code where code = e.vaccum AND code_group='vaccum') as vaccum_name, DATE_FORMAT(e.start_time, "%m-%d %H:%i") start_time, DATE_FORMAT(e.end_time, "%m-%d %H:%i") end_time, TIME_FORMAT(SEC_TO_TIME(unix_timestamp(end_time)-unix_timestamp(start_time)), '%H:%i') as d_time, ''+TIME_FORMAT(SEC_TO_TIME(unix_timestamp(end_time)-unix_timestamp(start_time)), '%H.%i') as unix_time, e.vaccum_group, (select code_name from common_code where code = e.vaccum_group AND code_group='jab_class') as vaccum_group_name, e.total_production, e.cycle_time, e.is_check, e.check_user, e.check_date, e.created, e.last_modified, ep.ep_id, ep.vehicle_id, IFNULL( ep.production, 0) production, IFNULL((ep.production - 0), 0) complete, IFNULL((1/ep.production*100), 0) defect_rate, vm.kind, vm.product_name, vm.model_car, vm.lh_rh, vm.product_id, vm.client, c.cnt, e.tunc_used, d.avgTime FROM packing_vaccum e LEFT JOIN packing_vaccum_produce ep ON (e.e_id=ep.e_id) LEFT JOIN vehicle_management vm ON (ep.vehicle_id = vm.vehicle_id) LEFT JOIN (SELECT b.e_id, count(b.e_id) cnt FROM (SELECT e.e_id FROM packing_vaccum e LEFT JOIN packing_vaccum_produce ep ON (e.e_id=ep.e_id) LEFT JOIN vehicle_management vm ON (ep.vehicle_id = vm.vehicle_id)
ORDER BY e.vaccum_date DESC, e.e_id, ep.ep_id
ORDER BY ${orderBy}
LIMIT #{startNum}, #{pageCutNum} ) b GROUP BY b.e_id ) c ON e.e_id = c.e_id JOIN (SELECT TIME_FORMAT(SEC_TO_TIME(avg(T1)), '%H:%i') as avgTime FROM (SELECT b.e_id, count(b.e_id) cnt, T1 FROM (SELECT e.e_id, @time := (unix_timestamp(end_time)-unix_timestamp(start_time)) T1 FROM packing_vaccum e LEFT JOIN packing_vaccum_produce ep ON (e.e_id=ep.e_id) LEFT JOIN vehicle_management vm ON (ep.vehicle_id = vm.vehicle_id)
ORDER BY e.vaccum_date DESC, e.e_id, ep.ep_id
ORDER BY ${orderBy}
LIMIT #{startNum}, #{pageCutNum} ) b GROUP BY b.e_id
) c ) d
ORDER BY e.vaccum_date DESC, e.e_id, ep.ep_id
ORDER BY ${orderBy}

SELECT e.e_id, DATE_FORMAT(e.vaccum_date, '%y-%m-%d') as vaccum_date, e.work_type, (select code_name from common_code where code = e.work_type AND code_group='work_type') as work_type_name, e.vaccum, (select code_name from common_code where code = e.vaccum AND code_group='vaccum') as vaccum_name, DATE_FORMAT(e.start_time, "%m-%d %H:%i") start_time, DATE_FORMAT(e.end_time, "%m-%d %H:%i") end_time, TIME_FORMAT(SEC_TO_TIME(unix_timestamp(end_time)-unix_timestamp(start_time)), '%H:%i') as d_time, ''+TIME_FORMAT(SEC_TO_TIME(unix_timestamp(end_time)-unix_timestamp(start_time)), '%H.%i') as unix_time, e.vaccum_group, (select code_name from common_code where code = e.vaccum_group AND code_group='jab_class') as vaccum_group_name, e.weight1, e.weight2, e.weight3, e.total_production, e.cycle_time, e.is_check, e.check_user, e.check_date, e.created, e.last_modified, ep.ep_id, ep.vehicle_id, IFNULL( ep.production, 0) production, IFNULL((ep.production - 1), 0) complete, IFNULL((1/ep.production*100), 0) defect_rate, vm.kind, vm.product_name, vm.model_car, vm.lh_rh, vm.product_id, vm.client, c.cnt, e.tunc_used FROM packing_vaccum e LEFT JOIN packing_vaccum_produce ep ON (e.e_id=ep.e_id) LEFT JOIN vehicle_management vm ON (ep.vehicle_id = vm.vehicle_id) LEFT JOIN (SELECT b.e_id, count(b.e_id) cnt FROM (SELECT e.e_id FROM packing_vaccum e LEFT JOIN packing_vaccum_produce ep ON (e.e_id=ep.e_id) LEFT JOIN vehicle_management vm ON (ep.vehicle_id = vm.vehicle_id)
ORDER BY e.vaccum_date DESC, e.e_id, ep.ep_id
ORDER BY ${orderBy}
LIMIT #{startNum}, #{pageCutNum} ) b GROUP BY b.e_id ) c ON e.e_id = c.e_id
ORDER BY e.vaccum_date DESC, e.e_id, ep.ep_id
ORDER BY ${orderBy}
LIMIT #{startNum}, #{pageCutNum}

    SELECT * FROM 
	    (SELECT vm.kind,
	        vm.model_car,
	        vm.product_name,
	        vm.lh_rh
	    FROM packing_vaccum e
	    LEFT JOIN packing_vaccum_produce ep ON (e.e_id=ep.e_id)
	    LEFT JOIN vehicle_management vm ON (ep.vehicle_id = vm.vehicle_id)
	    ) A


		AND A.kind = #{kind}

		AND A.model_car = #{modelCar}

		AND A.product_name = #{productName}

		AND A.lh_rh = #{lhRh}

mapper.update:
	UPDATE packing_vaccum
    SET last_modified = now()
    , vaccum_date = STR_TO_DATE(#{vaccumDate}, '%Y-%m-%d')
    , work_type = #{workType}
    , vaccum = #{vaccum}
    , start_time = STR_TO_DATE(#{startTime}, '%Y-%m-%d %H:%i')
    , end_time = STR_TO_DATE(#{endTime}, '%Y-%m-%d %H:%i')
    , vaccum_group = #{vaccumGroup}
    , total_production = #{totalProduction}
    , tunc_used = #{tuncUsed}
    WHERE e_id = #{eId}

    	UPDATE packing_vaccum_produce
	    SET last_modified = now()
	    , production = #{production}
	    WHERE e_id = #{eId} AND ep_id = #{epId}

        UPDATE packing_vaccum
	    SET is_check=#{isCheck}, check_user=#{checkUser}, check_date=now()
	    WHERE e_id = #{eId}
