mapper.select:
SELECT prod_dtl_nm 
  FROM HANADMIN.BOM_INFO mi 
 WHERE 1=1

   AND mi.kind = #{KIND}


   AND mi.prod_nm = #{PROD_NM}

SELECT prod_id 
  FROM HANADMIN.BOM_INFO mi 
 WHERE 1=1

   AND mi.kind = #{KIND}


   AND mi.prod_nm = #{PROD_NM}


   AND mi.prod_dtl_nm = #{PROD_DTL_NM}

/* BomInfoMapper.selectBomInfoList */ SELECT PROD_ID , KIND , PROD_NM , PROD_DTL_NM , THICKNESS_1 , THICKNESS_2 , THICKNESS_3 , THICKNESS_4 , THICKNESS_5 , THICKNESS_6 , MESH , MESH_OUT , BIGO , to_char(CREATE_DATE, 'YYYY.MM.DD') CREATE_DATE , CREATE_USER , to_char(UPDATE_DATE, 'YYYY.MM.DD') UPDATE_DATE , UPDATE_USER FROM hanadmin.bom_info WHERE 1=1
AND PROD_ID = #{PROD_ID}
AND KIND = #{KIND}
AND PROD_NM = #{PROD_NM}
AND PROD_DTL_NM = #{PROD_DTL_NM}
ORDER BY PROD_ID

mapper.insert:
/* BomInfoMapper.insertBomInfo */
	INSERT INTO hanadmin.bom_info
	(	
		PROD_ID
		, KIND
		, PROD_NM
		, PROD_DTL_NM
		, THICKNESS_1
		, THICKNESS_2
		, THICKNESS_3
		, THICKNESS_4
		, THICKNESS_5
		, THICKNESS_6
		, MESH
		, MESH_OUT
		, BIGO
		, CREATE_DATE
		, CREATE_USER
		, UPDATE_DATE
		, UPDATE_USER
	)
	VALUES
	(
		#{PROD_ID}
		, #{KIND}
		, #{PROD_NM}
		, #{PROD_DTL_NM}
		, #{THICKNESS_1}
		, #{THICKNESS_2}
		, #{THICKNESS_3}
		, #{THICKNESS_4}
		, #{THICKNESS_5}
		, #{THICKNESS_6}
		, #{MESH}
		, #{MESH_OUT}
		, #{BIGO}
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.update:
/* BomInfoMapper.updateBomInfo */
	UPDATE hanadmin.bom_info 
	   SET KIND = #{KIND}
		   , PROD_NM = #{PROD_NM}
		   , PROD_DTL_NM = #{PROD_DTL_NM}
		   , THICKNESS_1 = #{THICKNESS_1}
		   , THICKNESS_2 = #{THICKNESS_2}
		   , THICKNESS_3 = #{THICKNESS_3}
		   , THICKNESS_4 = #{THICKNESS_4}
		   , THICKNESS_5 = #{THICKNESS_5}
		   , THICKNESS_6 = #{THICKNESS_6}
		   , MESH = #{MESH}
		   , MESH_OUT = #{MESH_OUT}
		   , BIGO = #{BIGO}
		   , CREATE_DATE = SYSDATE
		   , CREATE_USER = #{CREATE_USER}
		   , UPDATE_DATE = SYSDATE
		   , UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	AND PROD_ID = #{PROD_ID}

mapper.delete:
/* BomInfoMapper.deleteBomInfo */
	DELETE FROM hanadmin.bom_info 
	 WHERE 1=1 

    	 AND PROD_ID = #{PROD_ID}

