mapper.select:
/* MtrlsInfoMapper.selectMtrlsInfoList */
	SELECT PROD_ID
		, KIND
		, PROD_NM
		, PROD_DTL_NM
		, to_char(CREATE_DATE, 'YYYY.MM.DD') CREATE_DATE
		, CREATE_USER
		, to_char(UPDATE_DATE, 'YYYY.MM.DD') UPDATE_DATE
		, UPDATE_USER
	FROM hanadmin.mtrls_info
   WHERE 1=1

   	   AND PROD_ID = #{PROD_ID}


   	   AND KIND = #{KIND}


   	   AND PROD_NM = #{PROD_NM}


   	   AND PROD_DTL_NM = #{PROD_DTL_NM}

   ORDER BY PROD_ID

mapper.insert:
/* MtrlsInfoMapper.insertMtrlsInfo */
	INSERT INTO hanadmin.mtrls_info
	(	
		PROD_ID
		, KIND
		, PROD_NM
		, PROD_DTL_NM
		, CREATE_DATE
		, CREATE_USER
		, UPDATE_DATE
		, UPDATE_USER
	)
	VALUES
	(
		#{PROD_ID}
		, #{KIND}
		, #{PROD_NM}
		, #{PROD_DTL_NM}
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.update:
/* MtrlsInfoMapper.updateMtrlsInfo */
	UPDATE hanadmin.mtrls_info 
	   SET KIND = #{KIND}
		   , PROD_NM = #{PROD_NM}
		   , PROD_DTL_NM = #{PROD_DTL_NM}
		   , CREATE_DATE = SYSDATE
		   , CREATE_USER = #{CREATE_USER}
		   , UPDATE_DATE = SYSDATE
		   , UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	AND PROD_ID = #{PROD_ID}

mapper.delete:
/* MtrlsInfoMapper.deleteMtrlsInfo */
	DELETE FROM hanadmin.mtrls_info 
	 WHERE 1=1 

    	 AND PROD_ID = #{PROD_ID}
