mapper.insert:
/* MtrlsInfoMapper.insertMtrlsInfo */
	INSERT INTO hanadmin.mtrls_info
	(	
		PROD_ID
		, KIND
		, PROD_NM
		, COST
		, PROD_WEIGHT
		, CREATE_DATE
		, CREATE_USER
		, UPDATE_DATE
		, UPDATE_USER
	)
	VALUES
	(
		#{PROD_ID}
		, #{KIND}
		, #{PROD_NM}
		, #{COST}
		, #{PROD_WEIGHT}
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.delete:
/* MtrlsInfoMapper.deleteMtrlsInfo */
	DELETE FROM hanadmin.mtrls_info 
	 WHERE 1=1 

    	 AND PROD_ID = #{PROD_ID}

mapper.update:
/* MtrlsInfoMapper.updateMtrlsInfo */
	UPDATE hanadmin.mtrls_info 
	   SET KIND = #{KIND}
		, PROD_NM = #{PROD_NM}
		, COST = #{COST}
		, PROD_WEIGHT = #{PROD_WEIGHT}
		, SYSDATE
		, CREATE_USER = #{CREATE_USER}
		, SYSDATE
		, UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	AND PROD_ID = #{PROD_ID}
