mapper.select:
/* PrdtInOutMapper.selectPrdtProdNm */
	SELECT DISTINCT info.PROD_NM CODE, info.PROD_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}

     ORDER BY CODE

/* PrdtInOutMapper.selectPrdtProdDtlNm */
	SELECT DISTINCT info.PROD_DTL_NM CODE, info.PROD_DTL_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}

     ORDER BY CODE

/* PrdtInOutMapper.selectPrdtProdId */
	SELECT info.PROD_ID CODE, info.PROD_ID NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

	SELECT info.OUT_PRICE CODE, info.OUT_PRICE NAME 
	  FROM hanadmin.prdt_info info
	 WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

/* PrdtInOutMapper.selectPrdtInOutList */ SELECT inout.IDX , inout.PRODUCT_GUBUN , (SELECT CODE_NAME FROM hanadmin.common_code WHERE CODE_GROUP = 'product_gubun' and CODE = inout.PRODUCT_GUBUN) PRODUCT_GUBUN_NM , inout.KIND , (SELECT CODE_NAME FROM hanadmin.common_code WHERE CODE_GROUP = 'product_kind' and CODE = inout.KIND) AS KIND_NM , info.PROD_NM , info.PROD_DTL_NM , inout.PROD_ID , inout.OUT_NUM , inout.TO_PLACE , (SELECT CODE_NAME FROM hanadmin.common_code WHERE CODE_GROUP = 'goal_city' and CODE = inout.TO_PLACE) AS TO_PLACE_NM , info.CLIENT , (SELECT CODE_NAME FROM hanadmin.common_code WHERE CODE_GROUP = 'client' and CODE = inout.CLIENT) AS CLIENT_NM , inout.INOUT_DATE , inout.BOX_COUNT , inout.BOX_PER_COUNT , inout.OUT_METHOD , inout.MAN , inout.PRICE AS PRICE , (inout.BOX_COUNT * inout.BOX_PER_COUNT) AS TOT_COUNT , (inout.PRICE * inout.BOX_PER_COUNT) AS TOT_PRICE , inout.BIGO , inout.IS_CHECK , inout.CREATE_DATE , inout.CREATE_USER , inout.UPDATE_DATE , inout.UPDATE_USER FROM hanadmin.prdt_inout inout, hanadmin.prdt_info info WHERE inout.PROD_ID = info.PROD_ID
AND inout.PRODUCT_GUBUN = #{PRODUCT_GUBUN}
AND info.CLIENT = #{CLIENT}
AND info.TO_PLACE = #{GOAL_CITY}
AND info.KIND = #{KIND}
AND info.PROD_NM = #{PROD_NM}
AND info.PROD_DTL_NM = #{PROD_DTL_NM}
AND inout.PROD_ID = #{PROD_ID}
AND REPLACE(inout.INOUT_DATE, '-', '') BETWEEN REPLACE(#{dateStart}, '-', '') AND REPLACE(#{dateEnd}, '-', '')
AND REPLACE(inout.INOUT_DATE, '-', '') BETWEEN REPLACE(#{dateStart}, '-', '') AND '99999999'
AND REPLACE(inout.INOUT_DATE, '-', '') BETWEEN '00000000' AND REPLACE(#{dateEnd}, '-', '')
ORDER BY inout.INOUT_DATE desc, inout.IDX

mapper.insert:
/* PrdtInOutMapper.insertPrdtInOut */
	INSERT INTO hanadmin.prdt_inout
	(	
		IDX
		, PRODUCT_GUBUN
		, KIND 
		, PROD_ID 
		, OUT_NUM
		, TO_PLACE 
		, CLIENT 
		, INOUT_DATE
		, BOX_COUNT
		, BOX_PER_COUNT
		, OUT_METHOD 
		, MAN 
		, PRICE 
		, BIGO
		, IS_CHECK
		, CREATE_DATE 
		, CREATE_USER 
		, UPDATE_DATE 
		, UPDATE_USER
	)
	VALUES
	(
		hanadmin.prdt_inout_seq.nextval
		, #{PRODUCT_GUBUN} 
		, #{KIND}  
		, (SELECT PROD_ID FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM})
		, #{OUT_NUM} 
		, #{TO_PLACE}
		, #{CLIENT}
		, #{INOUT_DATE} 
		, #{BOX_COUNT} 
		, #{BOX_PER_COUNT} 
		, #{OUT_METHOD} 
		, #{MAN} 
		, (SELECT OUT_PRICE FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM})
		, #{BIGO}
		, 'NO'
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.update:
/* PrdtInOutMapper.updatePrdtInOut */
	UPDATE hanadmin.prdt_inout
	   SET PRODUCT_GUBUN = #{PRODUCT_GUBUN} 
		   , KIND = #{KIND} 
		   , PROD_ID = (SELECT PROD_ID FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM})
		   , OUT_NUM = #{OUT_NUM} 
		   , TO_PLACE = #{TO_PLACE} 
		   , CLIENT = #{CLIENT}
		   , INOUT_DATE = #{INOUT_DATE} 
		   , BOX_COUNT = #{BOX_COUNT} 
		   , BOX_PER_COUNT = #{BOX_PER_COUNT} 
		   , OUT_METHOD = #{OUT_METHOD} 
		   , MAN = #{MAN} 
		   , PRICE = #{PRICE}
		   , BIGO = #{BIGO}
		   , IS_CHECK = #{IS_CHECK}
		   , CREATE_DATE = SYSDATE
		   , CREATE_USER = #{CREATE_USER}
		   , UPDATE_DATE = SYSDATE
		   , UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	AND IDX = #{IDX}

mapper.delete:
/* PrdtInOutMapper.deletePrdtInOut */
	DELETE FROM hanadmin.prdt_inout
	 WHERE 1=1 

    	 AND IDX = #{IDX}

mapper.update:
/* PrdtInOutMapper.deletePrdtStock */
	UPDATE hanadmin.prdt_stock  
	   SET COUNT = COUNT - (#{BOX_PER_COUNT})
		   , BOX_COUNT = BOX_COUNT - (#{BOX_COUNT})
		   , UPDATE_DATE = SYSDATE
		   , UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	AND PROD_ID = #{PROD_ID}
