mapper.select:
/* PrdtRepairMapper.selectPrdtProdNm */
	SELECT DISTINCT info.PROD_NM CODE, info.PROD_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}

     ORDER BY CODE

/* PrdtRepairMapper.selectPrdtProdDtlNm */
	SELECT DISTINCT info.PROD_DTL_NM CODE, info.PROD_DTL_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}

     ORDER BY CODE

/* PrdtRepairMapper.selectPrdtProdId */
	SELECT info.PROD_ID CODE, info.PROD_ID NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

	SELECT info.OUT_PRICE CODE, info.OUT_PRICE NAME 
	  FROM hanadmin.prdt_info info
	 WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

/* PrdtRepairMapper.selectPrdtRepairList */ SELECT repair.PRDT_DATE , repair.WORK_TYPE , repair.EQP_ID , repair.ST_TIME , repair.ED_TIME , repair.STOPPING_TIME , repair.BROKEN_PART , repair.IMG_URL , repair.PATH , repair.REASON , repair.METHOD , repair.MAN1 , repair.MAN2 , repair.REPAIR_MAN , repair.BIGO , repair.IS_CHECK , repair.CREATE_DATE , repair.CREATE_USER , repair.UPDATE_DATE , repair.UPDATE_USER FROM hanadmin.REPAIR_NOTES repair WHERE 1=1
AND repair.PRDT_DATE = #{PRDT_DATE}
AND repair.WORK_TYPE = #{WORK_TYPE}
AND repair.EQP_ID = #{EQP_ID}
AND REPLACE(repair.INOUT_DATE, '-', '') BETWEEN REPLACE(#{dateStart}, '-', '') AND REPLACE(#{dateEnd}, '-', '')
AND REPLACE(repair.INOUT_DATE, '-', '') BETWEEN REPLACE(#{dateStart}, '-', '') AND '99999999'
AND REPLACE(repair.INOUT_DATE, '-', '') BETWEEN '00000000' AND REPLACE(#{dateEnd}, '-', '')
ORDER BY repair.PRDT_DATE desc, repair.WORK_TYPE, repair.EQP_ID

mapper.insert:
/* PrdtRepairMapper.insertPrdtRepair */
	INSERT INTO hanadmin.REPAIR_NOTES
	(	
		PRDT_DATE
		, WORK_TYPE
		, EQP_ID 
		, ST_TIME 
		, ED_TIME
		, STOPPING_TIME 
		, BROKEN_PART 
		, IMG_URL
		, PATH
		, REASON
		, METHOD 
		, MAN1 
		, MAN2 
		, REPAIR_MAN 
		, BIGO
		, IS_CHECK
		, CREATE_DATE 
		, CREATE_USER 
		, UPDATE_DATE 
		, UPDATE_USER
	)
	VALUES
	(
		#{PRDT_DATE} 
		, #{WORK_TYPE}  
		, #{EQP_ID}
		, #{ST_TIME} 
		, #{ED_TIME}
		, #{STOPPING_TIME}
		, #{BROKEN_PART} 
		, #{IMG_URL} 
		, #{PATH} 
		, #{REASON} 
		, #{METHOD} 
		, #{MAN1} 
		, #{MAN2} 
		, #{REPAIR_MAN} 
		, #{BIGO}
		, 'NO'
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.update:
/* PrdtRepairMapper.updatePrdtRepair */
	UPDATE hanadmin.REPAIR_NOTES
	   SET ST_TIME = #{ST_TIME} 
		   , ED_TIME = #{ED_TIME} 
		   , STOPPING_TIME = #{STOPPING_TIME}
		   , BROKEN_PART = #{BROKEN_PART} 
		   , IMG_URL = #{IMG_URL} 
		   , PATH = #{PATH}
		   , REASON = #{REASON} 
		   , METHOD = #{METHOD} 
		   , MAN1 = #{MAN1} 
		   , MAN2 = #{MAN2} 
		   , REPAIR_MAN = #{REPAIR_MAN} 
		   , BIGO = #{BIGO}
		   , IS_CHECK = #{IS_CHECK}
		   , CREATE_DATE = SYSDATE
		   , CREATE_USER = #{CREATE_USER}
		   , UPDATE_DATE = SYSDATE
		   , UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	AND PRDT_DATE = #{PRDT_DATE}


    	AND WORK_TYPE = #{WORK_TYPE}


    	AND EQP_ID = #{EQP_ID}

mapper.delete:
/* PrdtRepairMapper.deletePrdtRepair */
	DELETE FROM hanadmin.REPAIR_NOTES
	 WHERE 1=1 

    	AND PRDT_DATE = #{PRDT_DATE}


    	AND WORK_TYPE = #{WORK_TYPE}


    	AND EQP_ID = #{EQP_ID}
