mapper.select:
/* ProductionCoatingMapper.selectPrdtCoatingNm */
	SELECT DISTINCT info.PROD_NM CODE, info.PROD_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}

     ORDER BY CODE

/* ProductionCoatingMapper.selectPrdtCoatingDtlNm */
	SELECT DISTINCT info.PROD_DTL_NM CODE, info.PROD_DTL_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}

     ORDER BY CODE

/* ProductionCoatingMapper.selectPrdtCoatingId */
	SELECT info.PROD_ID CODE, info.PROD_ID NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

	/* ProductionCoatingMapper.getPrice */
	SELECT info.OUT_PRICE CODE, info.OUT_PRICE NAME 
	  FROM hanadmin.prdt_info info
	 WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

/* ProductionCoatingMapper.selectPrdtCoatingList */ SELECT to_char(to_date(s.PRDT_DATE), 'YY-MM-DD') PRDT_DATE , s.WORK_TYPE , s.EQP_ID , s.AREA , info.KIND , (SELECT CODE_NAME FROM hanadmin.common_code WHERE CODE_GROUP = 'product_kind' and CODE = info.KIND) AS KIND_NM , info.PROD_NM , info.PROD_DTL_NM , s.PROD_ID , s.UPH , s.ST_TIME , s.ED_TIME , s.STOPPING_TIME , s.REST_TIME , ROUND((TO_DATE(s.ED_TIME, 'YY-MM-DD HH24:MI') - TO_DATE(s.ST_TIME, 'YY-MM-DD HH24:MI')) * 24, 2) WORK_TIME
, s.PRDT_CNT , s.FAULTY_CNT , s.PRDT_CNT - s.FAULTY_CNT FINISHED_CNT
, TRUNC(NVL((s.PRDT_CNT - s.FAULTY_CNT)/(DECODE(UPH, 0, 1, UPH)*(DECODE(TO_DATE(s.ED_TIME, 'YY-MM-DD HH24:MI') - TO_DATE(s.ST_TIME, 'YY-MM-DD HH24:MI'), 0, NULL, (TO_DATE(s.ED_TIME, 'YY-MM-DD HH24:MI') - TO_DATE(s.ST_TIME, 'YY-MM-DD HH24:MI'))*24)- s.REST_TIME))*100, 0), 2) CMPLT_PER , TRUNC(NVL((s.PRDT_CNT - s.FAULTY_CNT)/(DECODE(UPH, 0, 1, UPH)*(DECODE(TO_DATE(s.ED_TIME, 'YY-MM-DD HH24:MI') - TO_DATE(s.ST_TIME, 'YY-MM-DD HH24:MI'), 0, NULL, (TO_DATE(s.ED_TIME, 'YY-MM-DD HH24:MI') - TO_DATE(s.ST_TIME, 'YY-MM-DD HH24:MI'))*24)- s.REST_TIME - STOPPING_TIME))*100, 0), 2) MAN_CMPLT_PER , POWDER_COLOR , POWDER_CNT , SUM_WEIGHT , PWD_WEIGHT , POWDER_CNT - SUM_WEIGHT POWDER_USAGE , POWDER_CNT - SUM_WEIGHT - PWD_WEIGHT DIFF
, s.IS_CHECK
FROM HANADMIN.PRDT_STATUS_COAT s, HANADMIN.PRDT_INFO info WHERE s.PROD_ID = info.PROD_ID
AND WORK_TYPE = #{WORK_TYPE}
AND EQP_ID = #{EQP_ID}
AND info.KIND = #{KIND}
AND info.PROD_NM = #{PROD_NM}
AND info.PROD_DTL_NM = #{PROD_DTL_NM}
AND s.PROD_ID = #{PROD_ID}
AND '20'||PRDT_DATE BETWEEN #{dateStart} AND #{dateEnd}
AND '20'||PRDT_DATE BETWEEN #{dateStart} AND '99-99-99'
AND '20'||PRDT_DATE BETWEEN '00-00-00' AND #{dateEnd}
ORDER BY s.PRDT_DATE desc

mapper.insert:
/* ProductionCoatingMapper.insertPrdtStatusCoating */
	INSERT INTO hanadmin.prdt_status_coat
	(	
		PRDT_DATE 
		, WORK_TYPE
		, EQP_ID
		, AREA
		, PROD_ID
		, UPH
		, ST_TIME
		, ED_TIME
		, STOPPING_TIME
		, REST_TIME
		, PRDT_CNT
		, FAULTY_CNT
		, POWDER_COLOR
	    , POWDER_CNT
	    , SUM_WEIGHT
	    , PWD_WEIGHT
		, BIGO
		, IS_CHECK
		, CREATE_DATE 
		, CREATE_USER 
		, UPDATE_DATE 
		, UPDATE_USER
	)
	VALUES
	(
		#{PRDT_DATE}
		, #{WORK_TYPE} 
		, #{EQP_ID}  
		, #{AREA}
		, (SELECT PROD_ID FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM}) 
		, #{UPH}
		, #{ST_TIME}
		, #{ED_TIME} 
		, #{STOPPING_TIME} 
		, #{REST_TIME} 
		, #{PRDT_CNT} 
		, #{FAULTY_CNT}
		, #{POWDER_COLOR}
	    , #{POWDER_CNT}
	    , (SELECT SUM_WEIGHT FROM hanadmin.bom_info_coating WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM})
	    , (SELECT PWD_WEIGHT FROM hanadmin.bom_info_coating WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM}) 
		, #{BIGO}
		, #{IS_CHECK}
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.update:
/* ProductionCoatingMapper.updatePrdtStatusCoating */
	UPDATE hanadmin.prdt_status_coat
	   SET PRDT_DATE = #{PRDT_DATE}
		, WORK_TYPE = #{WORK_TYPE}
		, EQP_ID = #{EQP_ID}
		, AREA = #{AREA}
		, PROD_ID = #{PROD_ID}
		, UPH = #{UPH}
		, ST_TIME = #{ST_TIME}
		, ED_TIME = #{ED_TIME}
		, STOPPING_TIME = #{STOPPING_TIME}
		, REST_TIME = #{REST_TIME}
		, PRDT_CNT = #{PRDT_CNT}
		, FAULTY_CNT = #{FAULTY_CNT}
		, POWDER_COLOR = #{POWDER_COLOR}
		, POWDER_CNT = #{POWDER_CNT}
		, SUM_WEIGHT = #{SUM_WEIGHT}
		, PWD_WEIGHT = #{PWD_WEIGHT}
		, BIGO = #{BIGO}
		, IS_CHECK = #{IS_CHECK}
		, CREATE_DATE = SYSDATE
		, CREATE_USER = #{CREATE_USER} 
		, UPDATE_DATE = SYSDATE
		, UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND WORK_TYPE = #{WORK_TYPE}


    	 AND EQP_ID = #{EQP_ID}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}

mapper.delete:
/* ProductionCoatingMapper.deletePrdtStatusCoating */
	DELETE FROM hanadmin.prdt_status_coat
	 WHERE 1=1 

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND WORK_TYPE = #{WORK_TYPE}


    	 AND EQP_ID = #{EQP_ID}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}
