mapper.select:
/* ProductionProdMapper.selectPrdtProdNm */
	SELECT DISTINCT info.PROD_NM CODE, info.PROD_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}

     ORDER BY CODE

/* ProductionProdMapper.selectPrdtProdDtlNm */
	SELECT DISTINCT info.PROD_DTL_NM CODE, info.PROD_DTL_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}

     ORDER BY CODE

/* ProductionProdMapper.selectPrdtProdId */
	SELECT info.PROD_ID CODE, info.PROD_ID NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

	/* ProductionProdMapper.getPrice */
	SELECT info.OUT_PRICE CODE, info.OUT_PRICE NAME 
	  FROM hanadmin.prdt_info info
	 WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

mapper.insert:
/* ProductionProdMapper.insertPrdtStatusProd */
	INSERT INTO hanadmin.prdt_status_prod
	(	
		PRDT_DATE 
		, WORK_TYPE
		, EQP_ID
		, AREA
		, PROD_ID
		, UPH
		, ST_TIME
		, ED_TIME
		, PRDT_CNT
		, FAULTY_CNT
		, FINISHED_CNT
		, CREATE_DATE 
		, CREATE_USER 
		, UPDATE_DATE 
		, UPDATE_USER
	)
	VALUES
	(
		#{PRDT_DATE}
		, #{WORK_TYPE} 
		, #{EQP_ID}  
		, #{AREA}
		, (SELECT PROD_ID FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM}) 
		, #{UPH}
		, #{ST_TIME}
		, #{ED_TIME} 
		, #{PRDT_CNT} 
		, #{FAULTY_CNT} 
		, #{FINISHED_CNT} 
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

/* ProductionProdMapper.insertPrdtMtrlsUsage */
	INSERT INTO hanadmin.mtrls_usage
	(	
		PRDT_DATE 
		, WORK_TYPE
		, EQP_ID
		, AREA
	    , PROD_ID
	    , BOM_ID 
		, CREATE_DATE 
		, CREATE_USER 
		, UPDATE_DATE 
		, UPDATE_USER
	)
	VALUES
	(
		#{PRDT_DATE} 
		, #{WORK_TYPE}  
		, #{EQP_ID} 
		, #{AREA}
		, #{PROD_ID}
		, #{BOM_ID} 
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.update:
/* ProductionProdMapper.updatePrdtStatusProd */
	UPDATE hanadmin.prdt_status_prod
	   SET PRODUCT_GUBUN = #{PRODUCT_GUBUN} 
		   , KIND = #{KIND} 
		   , PROD_ID = (SELECT PROD_ID FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM})
		   , OUT_NUM = #{OUT_NUM} 
		   , TO_PLACE = #{TO_PLACE} 
		   , CLIENT = #{CLIENT}
		   , INOUT_DATE = #{INOUT_DATE} 
		   , BOX_COUNT = #{BOX_COUNT} 
		   , BOX_PER_COUNT = #{BOX_PER_COUNT} 
		   , OUT_METHOD = #{OUT_METHOD} 
		   , MAN = #{MAN} 
		   , PRICE = #{PRICE}
		   , BIGO = #{BIGO}
		   , IS_CHECK = #{IS_CHECK}
		   , CREATE_DATE = SYSDATE
		   , CREATE_USER = #{CREATE_USER}
		   , UPDATE_DATE = SYSDATE
		   , UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND WORK_TYPE = #{WORK_TYPE}


    	 AND EQP_ID = #{EQP_ID}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}

/* ProductionProdMapper.updatePrdtMtrlsUsage */
	UPDATE hanadmin.mtrls_usage
	   SET PRODUCT_GUBUN = #{PRODUCT_GUBUN} 
		   , KIND = #{KIND} 
		   , PROD_ID = (SELECT PROD_ID FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM})
		   , OUT_NUM = #{OUT_NUM} 
		   , TO_PLACE = #{TO_PLACE} 
		   , CLIENT = #{CLIENT}
		   , INOUT_DATE = #{INOUT_DATE} 
		   , BOX_COUNT = #{BOX_COUNT} 
		   , BOX_PER_COUNT = #{BOX_PER_COUNT} 
		   , OUT_METHOD = #{OUT_METHOD} 
		   , MAN = #{MAN} 
		   , PRICE = #{PRICE}
		   , BIGO = #{BIGO}
		   , IS_CHECK = #{IS_CHECK}
		   , CREATE_DATE = SYSDATE
		   , CREATE_USER = #{CREATE_USER}
		   , UPDATE_DATE = SYSDATE
		   , UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND WORK_TYPE = #{WORK_TYPE}


    	 AND EQP_ID = #{EQP_ID}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}


    	 AND BOM_ID = #{BOM_ID}

mapper.delete:
/* ProductionProdMapper.deletePrdtStatusProd */
	DELETE FROM hanadmin.prdt_status_prod
	 WHERE 1=1 

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND WORK_TYPE = #{WORK_TYPE}


    	 AND EQP_ID = #{EQP_ID}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}

/* ProductionProdMapper.deletePrdtMtrlsUsage */
	DELETE FROM hanadmin.mtrls_usage
	 WHERE 1=1

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND WORK_TYPE = #{WORK_TYPE}


    	 AND EQP_ID = #{EQP_ID}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}


    	 AND BOM_ID = #{BOM_ID}
