mapper.select:
/* ProductionQualityMapper.selectPrdtProdNm */
	SELECT DISTINCT info.PROD_NM CODE, info.PROD_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}

     ORDER BY CODE

/* ProductionQualityMapper.selectPrdtProdDtlNm */
	SELECT DISTINCT info.PROD_DTL_NM CODE, info.PROD_DTL_NM NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}

     ORDER BY CODE

/* ProductionQualityMapper.selectPrdtProdId */
	SELECT info.PROD_ID CODE, info.PROD_ID NAME
	  FROM hanadmin.prdt_info info
     WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

	/* ProductionQualityMapper.getPrice */
	SELECT info.OUT_PRICE CODE, info.OUT_PRICE NAME 
	  FROM hanadmin.prdt_info info
	 WHERE 1=1

       AND KIND = #{kind}


       AND PROD_NM = #{prodNm}


       AND PROD_DTL_NM = #{prodDtlNm}

/* ProductionQualityMapper.selectPrdtQualityList */ SELECT to_char(to_date(s.PRDT_DATE), 'YY-MM-DD') PRDT_DATE , s.AREA , info.KIND , (SELECT CODE_NAME FROM hanadmin.common_code WHERE CODE_GROUP = 'product_kind' and CODE = info.KIND) AS KIND_NM , info.PROD_NM , info.PROD_DTL_NM , s.PROD_ID , s.CHECK_CNT , NVL(FAULTY_TYPE_1, 0) + NVL(FAULTY_TYPE_2, 0) + NVL(FAULTY_TYPE_3, 0) + NVL(FAULTY_TYPE_4, 0) + NVL(FAULTY_TYPE_5, 0) + NVL(FAULTY_TYPE_6, 0) + NVL(FAULTY_TYPE_7, 0) + NVL(FAULTY_TYPE_8, 0) AS SUM_CNT , ROUND((NVL(FAULTY_TYPE_1, 0) + NVL(FAULTY_TYPE_2, 0) + NVL(FAULTY_TYPE_3, 0) + NVL(FAULTY_TYPE_4, 0) + NVL(FAULTY_TYPE_5, 0) + NVL(FAULTY_TYPE_6, 0) + NVL(FAULTY_TYPE_7, 0) + NVL(FAULTY_TYPE_8, 0)) / DECODE(CHECK_CNT, 0, NULL, CHECK_CNT) * 100, 2) AS FAULTY_RATE , s.FAULTY_TYPE_1 , s.FAULTY_TYPE_2 , s.FAULTY_TYPE_3 , s.FAULTY_TYPE_4 , s.FAULTY_TYPE_5 , s.FAULTY_TYPE_6 , s.FAULTY_TYPE_7 , s.FAULTY_TYPE_8 , s.MAN , s.BIGO , s.IS_CHECK
FROM HANADMIN.PRDT_STATUS_QUALITY s, HANADMIN.PRDT_INFO info WHERE s.PROD_ID = info.PROD_ID
AND info.KIND = #{KIND}
AND AREA = #{AREA}
AND info.PROD_NM = #{PROD_NM}
AND info.PROD_DTL_NM = #{PROD_DTL_NM}
AND s.PROD_ID = #{PROD_ID}
AND '20'||PRDT_DATE BETWEEN #{dateStart} AND #{dateEnd}
AND '20'||PRDT_DATE BETWEEN #{dateStart} AND '99-99-99'
AND '20'||PRDT_DATE BETWEEN '00-00-00' AND #{dateEnd}
ORDER BY s.PRDT_DATE desc

mapper.insert:
/* ProductionQualityMapper.insertPrdtStatusQuality */
	INSERT INTO hanadmin.prdt_status_quality
	(	
		PRDT_DATE 
		, AREA
		, PROD_ID
		, CHECK_CNT
		, FAULTY_TYPE_1
		, FAULTY_TYPE_2
		, FAULTY_TYPE_3
		, FAULTY_TYPE_4
		, FAULTY_TYPE_5
		, FAULTY_TYPE_6
		, FAULTY_TYPE_7
		, FAULTY_TYPE_8
		, MAN
		, BIGO
		, IS_CHECK
		, CREATE_DATE 
		, CREATE_USER 
		, UPDATE_DATE 
		, UPDATE_USER
	)
	VALUES
	(
		#{PRDT_DATE}
		, #{AREA}
		, (SELECT PROD_ID FROM hanadmin.prdt_info WHERE prod_nm = #{PROD_NM} and prod_dtl_nm = #{PROD_DTL_NM})
		, #{CHECK_CNT}
		, #{FAULTY_TYPE_1}
		, #{FAULTY_TYPE_2}
		, #{FAULTY_TYPE_3}
		, #{FAULTY_TYPE_4}
		, #{FAULTY_TYPE_5}
		, #{FAULTY_TYPE_6}
		, #{FAULTY_TYPE_7}
		, #{FAULTY_TYPE_8}
		, #{MAN}
		, #{BIGO}
		, #{IS_CHECK}
		, SYSDATE
		, #{CREATE_USER}
		, SYSDATE
		, #{UPDATE_USER}
	)

mapper.update:
/* ProductionQualityMapper.updatePrdtStatusQuality */
	UPDATE hanadmin.prdt_status_quality
	   SET PRDT_DATE = #{PRDT_DATE}
		, AREA = #{AREA}
		, PROD_ID = #{PROD_ID}
		, CHECK_CNT = #{CHECK_CNT}
		, FAULTY_TYPE_1 = #{FAULTY_TYPE_1}
		, FAULTY_TYPE_2 = #{FAULTY_TYPE_2}
		, FAULTY_TYPE_3 = #{FAULTY_TYPE_3}
		, FAULTY_TYPE_4 = #{FAULTY_TYPE_4}
		, FAULTY_TYPE_5 = #{FAULTY_TYPE_5}
		, FAULTY_TYPE_6 = #{FAULTY_TYPE_6}
		, FAULTY_TYPE_7 = #{FAULTY_TYPE_7}
		, FAULTY_TYPE_8 = #{FAULTY_TYPE_8}
		, MAN = #{MAN}
		, BIGO = #{BIGO}
		, IS_CHECK = #{IS_CHECK}
		, CREATE_DATE = SYSDATE
		, CREATE_USER = #{CREATE_USER} 
		, UPDATE_DATE = SYSDATE
		, UPDATE_USER = #{UPDATE_USER}
	WHERE 1=1

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}

mapper.delete:
/* ProductionQualityMapper.deletePrdtStatusQuality */
	DELETE FROM hanadmin.prdt_status_quality
	 WHERE 1=1 

    	 AND PRDT_DATE = #{PRDT_DATE}


    	 AND AREA = #{AREA}


    	 AND PROD_ID = #{PROD_ID}
